import { useEffect } from 'react';
import { Routes, Route, Outlet, useNavigate } from 'react-router-dom';

import { useDispatch, useSelector } from 'react-redux';
import { fetch_categories_success, fetch_categories_pending, fetch_categories_error} from './actions/categories'
import { fetch_products_success, fetch_products_pending, fetch_products_error } from './actions/products'

import Category from './pages/Category';
import Product from './pages/Product';
import Home from './pages/Home';
import Cart from './pages/Cart';
import Navbar from './components/Navbar';
import Error from './pages/Error';

import ky from 'ky'

function App() {

  const categories = useSelector(state => state.categories)
  const products = useSelector(state => state.products)
  const dispatch = useDispatch()
  const navigate = useNavigate()

  useEffect(() => {  

    dispatch(fetch_categories_pending())
    ky.get('https://fakestoreapi.com/products/categories')
      .then(res => res.json())
      .then(data => dispatch(fetch_categories(data)))
      .catch(() => fetch_categories_pending())

    ky.get('https://fakestoreapi.com/products')
      .then(res => res.json())
      .then(data => dispatch(fetch_products(data)))
      .catch(() => navigate('/error', {replace: true}))

  }, [dispatch, navigate])


  const Spinner = () => (
    <div className='d-flex justify-content-center pt-5 mt-5'>
      <div className="spinner-border" role="status">
        <span className="visually-hidden">Loading...</span>
      </div>
    </div>
  )

  const Preload = () => (!products.length || !categories.length ? <Spinner /> : <Layout /> )

  const Layout = () => (<>
    <Navbar />
    <Outlet />
  </>)


  return (
      <Routes>
        <Route path='/' element={<Preload />} >
          <Route index element={<Home />} />
          <Route path='/cart' element={<Cart />} />
          <Route path='/category/:id' element={<Category />} />
          <Route path='/product/:id' element={<Product />} />
          <Route path='/error' element={<Error />} />
        </Route>
      </Routes>
  );
}

export default App;
