import { BrowserRouter, Routes, Route, Outlet, useNavigate } from 'react-router-dom';

import Category from './pages/Category';
import Product from './pages/Product';
import Home from './pages/Home';
import Navbar from './components/Navbar';

import { useDispatch } from 'react-redux';

import { fetch_categories } from './actions/categories'
// import { fetch_products } from './actions/products'
import { useEffect } from 'react';

function App() {

  const dispatch = useDispatch()

  useEffect(() => {
    fetchData('https://fakestoreapi.com/products/categories')
      .then( data => fetch_categories(data))

  },[])

  const fetchData = async (URL) => {
    const response = await fetch(URL)
    const data = await response.json()
    console.log(data);
    if (data.error) return console.error('Failed to fetch data from', URL);
    return data
  }

  const Layout = () => (<>
    <Navbar />
    <Outlet />
  </>)

  return (
    <BrowserRouter>
      <Routes>
        <Route path='/' element={<Layout />} >
          <Route index element={<Home />} />
          <Route path='/category/:id' element={<Category />} />
          <Route path='/product/:id' element={<Product />} />
        </Route>
      </Routes>
    </BrowserRouter>
  );
}

export default App;
