const cart = (state = [], action) => {
  switch(action.type){
    case 'ADD_TO_CART' : return [...state, action.payload]
    case 'DELETE_FROM_CART' : return state.filter(product => product.id !== action.payload.id)
    case 'CLEAR_CART' : return []
    default : return state
  }
}

export default cart
