import '../styles/Product.scss'
import { IoCartOutline } from 'react-icons/io5'
import { useDispatch, useSelector } from 'react-redux'
import { addToCart, removeFromCart } from '../actions/cart'

const Product = ({product}) => {

  const dispatch = useDispatch()
  const cart = useSelector(state => state.cart)

  const AddCartBtn = () => (<button onClick={() => dispatch(addToCart(product))} className="btn btn-sm btn-outline-primary">Add to cart <IoCartOutline/> </button>)
  const RemoveCartBtn = () => (<button onClick={() => dispatch(removeFromCart(product))} className="btn btn-sm btn-outline-primary">Add to cart <IoCartOutline/> </button>)

  return (
    <div className="product col-2" >
      <img src={product.image} alt="product" className="product-image"  />
      <div className="product-title">{product.title}</div>
      <div className="product-price">
        {product.price}$
        { cart && cart.includes(product) ? <RemoveCartBtn /> : <AddCartBtn /> }
      </div>
    </div>
  )
}

export default Product
